mapper.update:
update general_users
set name = #{name}, postal_code=#{postal_code}, address1=#{address1}, address2=#{address2}, tel1=#{tel1}, tel2=#{tel2},
    tel3=#{tel3}
where email = #{email}

update general_users
	set exit_reason = #{exit_reason}
	where email = #{email} limit 1

update general_users
	set resume = #{resume}
	where email = #{email} limit 1
